import React, { useState, useEffect } from "react";
import axios from "axios";

import { QrReader } from "react-qr-reader";
import { showSuccessToast, showErrorToast } from "./utils/swal";
import {
  Clock,
  LogOut,
  Users,
  CheckCircle,
  XCircle,
  AlertCircle,
  Camera,
  X,
  UserCheck,
  Star,
  Award,
} from "lucide-react";

// API Service
const API_BASE_URL = "http://localhost:5000/api";

const api = axios.create({
  baseURL: API_BASE_URL,
});

// Add token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Auth Service
const authService = {
  login: async (email, password) => {
    const response = await api.post("/auth/login", { email, password });
    if (response.data.token) {
      localStorage.setItem("token", response.data.token);
      localStorage.setItem("user", JSON.stringify(response.data.user));
    }
    return response.data;
  },

  register: async (userData) => {
    const response = await api.post("/auth/register", userData);
    if (response.data.token) {
      localStorage.setItem("token", response.data.token);
      localStorage.setItem("user", JSON.stringify(response.data.user));
    }
    return response.data;
  },

  logout: () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
  },

  getCurrentUser: () => {
    const user = localStorage.getItem("user");
    return user ? JSON.parse(user) : null;
  },

  getQRCode: async () => {
    const response = await api.get("/auth/qr-code");
    return response.data;
  },
};

// Attendance Service
const attendanceService = {
  clockIn: async (qrCodeData, location) => {
    const response = await api.post("/attendance/clock-in", {
      qrCodeData,
      location,
    });
    return response.data;
  },

  clockOut: async () => {
    const response = await api.post("/attendance/clock-out");
    return response.data;
  },

  getHistory: async (params) => {
    const response = await api.get("/attendance/history", { params });
    return response.data;
  },

  getTodayStatus: async () => {
    const response = await api.get("/attendance/today");
    return response.data;
  },
};

// Components
const LoginForm = ({ onLogin, onToggleForm }) => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await authService.login(
        formData.email,
        formData.password,
      );
      showSuccessToast("Login berhasil!");
      onLogin(response.user);
    } catch (error) {
      showErrorToast(
        (error.response && error.response.data && error.response.data.msg) ||
          "Login gagal",
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 via-blue-700 to-indigo-800 flex items-center justify-center p-4 relative overflow-hidden">
      {/* Background decorative elements */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute top-10 left-10 w-72 h-72 bg-white rounded-full mix-blend-overlay filter blur-xl animate-pulse"></div>
        <div className="absolute top-40 right-10 w-96 h-96 bg-white rounded-full mix-blend-overlay filter blur-xl animate-pulse delay-1000"></div>
        <div className="absolute -bottom-32 left-1/2 transform -translate-x-1/2 w-96 h-96 bg-white rounded-full mix-blend-overlay filter blur-xl animate-pulse delay-2000"></div>
      </div>

      <div className="max-w-md w-full bg-white/95 backdrop-blur-sm rounded-2xl shadow-2xl p-8 relative z-10 border border-white/20">
        {/* Logo/Header */}
        <div className="text-center mb-8">
          <div className="w-20 h-20 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-lg">
            <UserCheck className="text-white" size={36} />
          </div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-gray-800 to-gray-600 bg-clip-text text-transparent mb-2">
            SchoolAttend
          </h1>
          <p className="text-gray-600 text-sm">
            Sistem Absensi Digital Terpadu
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="space-y-2">
            <label className="block text-sm font-semibold text-gray-700">
              Email
            </label>
            <div className="relative">
              <input
                type="email"
                required
                className="w-full px-4 py-3.5 bg-gray-50/80 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 hover:bg-white"
                value={formData.email}
                onChange={(e) =>
                  setFormData({ ...formData, email: e.target.value })
                }
                placeholder="nama@sekolah.edu"
              />
            </div>
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-semibold text-gray-700">
              Password
            </label>
            <div className="relative">
              <input
                type="password"
                required
                className="w-full px-4 py-3.5 bg-gray-50/80 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 hover:bg-white"
                value={formData.password}
                onChange={(e) =>
                  setFormData({ ...formData, password: e.target.value })
                }
                placeholder="••••••••"
              />
            </div>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 text-white py-3.5 rounded-xl hover:from-blue-600 hover:to-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
          >
            {loading ? (
              <div className="flex items-center justify-center">
                <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin mr-2"></div>
                Memverifikasi...
              </div>
            ) : (
              <div className="flex items-center justify-center">
                <UserCheck className="mr-2" size={20} />
                Masuk Sekarang
              </div>
            )}
          </button>
        </form>

        <div className="mt-6 text-center">
          <p className="text-gray-600 text-sm">
            Belum terdaftar?{" "}
            <button
              onClick={onToggleForm}
              className="text-blue-600 hover:text-blue-800 font-semibold hover:underline transition-colors"
            >
              Daftar sekarang
            </button>
          </p>
        </div>

        <div className="mt-8 p-4 bg-gradient-to-r from-gray-50 to-blue-50/30 rounded-xl border border-gray-200">
          <div className="flex items-center mb-2">
            <Star className="text-yellow-500 mr-2" size={16} />
            <p className="text-sm font-semibold text-gray-700">Demo Access</p>
          </div>
          <div className="text-xs text-gray-600 space-y-1">
            <p>
              <span className="font-medium">Admin:</span> admin@admin.com /
              admin123
            </p>
            <p>
              <span className="font-medium">Siswa:</span> Registrasi akun baru
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

const RegisterForm = ({ onRegister, onToggleForm }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    employeeId: "",
    department: "",
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await authService.register(formData);
      showSuccessToast("Registrasi berhasil!");
      onRegister(response.user);
    } catch (error) {
      showErrorToast(
        (error.response && error.response.data && error.response.data.msg) ||
          "Registrasi gagal",
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-600 via-green-600 to-teal-700 flex items-center justify-center p-4 relative overflow-hidden">
      {/* Background decorative elements */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute top-20 right-20 w-64 h-64 bg-white rounded-full mix-blend-overlay filter blur-xl animate-pulse"></div>
        <div className="absolute bottom-20 left-20 w-80 h-80 bg-white rounded-full mix-blend-overlay filter blur-xl animate-pulse delay-1000"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-72 h-72 bg-white rounded-full mix-blend-overlay filter blur-xl animate-pulse delay-2000"></div>
      </div>

      <div className="max-w-md w-full bg-white/95 backdrop-blur-sm rounded-2xl shadow-2xl p-8 relative z-10 border border-white/20">
        <div className="text-center mb-8">
          <div className="w-20 h-20 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-lg">
            <Users className="text-white" size={36} />
          </div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-gray-800 to-gray-600 bg-clip-text text-transparent mb-2">
            Bergabung
          </h1>
          <p className="text-gray-600 text-sm">
            Daftar untuk memulai absensi digital
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <label className="block text-sm font-semibold text-gray-700">
              Nama Lengkap
            </label>
            <input
              type="text"
              required
              className="w-full px-4 py-3 bg-gray-50/80 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-emerald-500 transition-all duration-200 hover:bg-white"
              value={formData.name}
              onChange={(e) =>
                setFormData({ ...formData, name: e.target.value })
              }
              placeholder="John Doe"
            />
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-semibold text-gray-700">
              Email
            </label>
            <input
              type="email"
              required
              className="w-full px-4 py-3 bg-gray-50/80 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-emerald-500 transition-all duration-200 hover:bg-white"
              value={formData.email}
              onChange={(e) =>
                setFormData({ ...formData, email: e.target.value })
              }
              placeholder="john@sekolah.edu"
            />
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-semibold text-gray-700">
              Password
            </label>
            <input
              type="password"
              required
              minLength={6}
              className="w-full px-4 py-3 bg-gray-50/80 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-emerald-500 transition-all duration-200 hover:bg-white"
              value={formData.password}
              onChange={(e) =>
                setFormData({ ...formData, password: e.target.value })
              }
              placeholder="Minimal 6 karakter"
            />
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-semibold text-gray-700">
              ID Siswa/Pegawai
            </label>
            <input
              type="text"
              required
              className="w-full px-4 py-3 bg-gray-50/80 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-emerald-500 transition-all duration-200 hover:bg-white"
              value={formData.employeeId}
              onChange={(e) =>
                setFormData({
                  ...formData,
                  employeeId: e.target.value.toUpperCase(),
                })
              }
              placeholder="STD001 / PGW001"
            />
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-semibold text-gray-700">
              Kelas/Departemen
            </label>
            <input
              type="text"
              className="w-full px-4 py-3 bg-gray-50/80 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-emerald-500 transition-all duration-200 hover:bg-white"
              value={formData.department}
              onChange={(e) =>
                setFormData({ ...formData, department: e.target.value })
              }
              placeholder="XII IPA 1 / Administrasi (opsional)"
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-gradient-to-r from-emerald-500 to-teal-600 text-white py-3.5 rounded-xl hover:from-emerald-600 hover:to-teal-700 disabled:opacity-50 transition-all duration-200 font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 mt-6"
          >
            {loading ? (
              <div className="flex items-center justify-center">
                <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin mr-2"></div>
                Mendaftar...
              </div>
            ) : (
              <div className="flex items-center justify-center">
                <Users className="mr-2" size={20} />
                Daftar Sekarang
              </div>
            )}
          </button>
        </form>

        <div className="mt-6 text-center">
          <p className="text-gray-600 text-sm">
            Sudah punya akun?{" "}
            <button
              onClick={onToggleForm}
              className="text-emerald-600 hover:text-emerald-800 font-semibold hover:underline transition-colors"
            >
              Masuk
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

const QRScanner = ({ onScan, onClose }) => {
  const [error, setError] = useState("");

  const handleScan = (result, error) => {
    if (result) {
      onScan(result.text);
    }
    if (error) {
      setError("Error scanning QR code");
    }
  };

  return (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-2xl max-w-md w-full p-6 shadow-2xl">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center">
            <div className="w-10 h-10 bg-blue-500 rounded-xl flex items-center justify-center mr-3">
              <Camera className="text-white" size={20} />
            </div>
            <h3 className="text-xl font-bold text-gray-900">Scan QR Code</h3>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 hover:bg-gray-100 p-2 rounded-xl transition-colors"
          >
            <X size={24} />
          </button>
        </div>

        <div className="mb-6 rounded-xl overflow-hidden bg-gray-100">
          <QrReader
            onResult={handleScan}
            constraints={{ facingMode: "environment" }}
            videoStyle={{ width: "100%", borderRadius: "12px" }}
          />
        </div>

        {error && (
          <div className="bg-red-50 border border-red-200 rounded-xl p-4 mb-4">
            <div className="flex items-center">
              <AlertCircle className="text-red-500 mr-2" size={20} />
              <span className="text-red-700 text-sm font-medium">{error}</span>
            </div>
          </div>
        )}

        <div className="text-center">
          <div className="inline-flex items-center px-4 py-2 bg-blue-50 rounded-xl">
            <div className="w-2 h-2 bg-blue-500 rounded-full mr-2 animate-pulse"></div>
            <span className="text-blue-700 text-sm font-medium">
              Arahkan kamera ke QR Code untuk absensi
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

const AttendanceCard = ({ onRefresh }) => {
  const [todayAttendance, setTodayAttendance] = useState(null);
  const [qrCode, setQrCode] = useState("");
  const [showScanner, setShowScanner] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadTodayAttendance();
    loadQRCode();
  }, []);

  const loadTodayAttendance = async () => {
    try {
      const response = await attendanceService.getTodayStatus();
      setTodayAttendance(response.attendance);
    } catch (error) {
      console.error("Error loading attendance:", error);
    }
  };

  const loadQRCode = async () => {
    try {
      const response = await authService.getQRCode();
      setQrCode(response.qrCode);
    } catch (error) {
      console.error("Error loading QR code:", error);
    }
  };

  const handleScan = async (qrData) => {
    setLoading(true);
    setShowScanner(false);

    try {
      const response = await attendanceService.clockIn(qrData);
      showSuccessToast(response.msg);
      loadTodayAttendance();
      onRefresh();
    } catch (error) {
      showErrorToast(
        (error.response && error.response.data && error.response.data.msg) ||
          "Absensi gagal",
      );
    } finally {
      setLoading(false);
    }
  };

  const handleClockOut = async () => {
    setLoading(true);

    try {
      const response = await attendanceService.clockOut();
      showSuccessToast(response.msg);
      loadTodayAttendance();
      onRefresh();
    } catch (error) {
      showErrorToast(
        (error.response && error.response.data && error.response.data.msg) ||
          "Clock out gagal",
      );
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "present":
        return "text-emerald-700 bg-emerald-100 border-emerald-200";
      case "late":
        return "text-amber-700 bg-amber-100 border-amber-200";
      case "absent":
        return "text-red-700 bg-red-100 border-red-200";
      default:
        return "text-gray-700 bg-gray-100 border-gray-200";
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case "present":
        return <CheckCircle size={16} className="text-emerald-600" />;
      case "late":
        return <Clock size={16} className="text-amber-600" />;
      case "absent":
        return <XCircle size={16} className="text-red-600" />;
      default:
        return <AlertCircle size={16} className="text-gray-600" />;
    }
  };

  const formatTime = (dateString) => {
    return new Date(dateString).toLocaleTimeString("id-ID", {
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  return (
    <>
      {showScanner && (
        <QRScanner onScan={handleScan} onClose={() => setShowScanner(false)} />
      )}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
        {/* Header with gradient */}
        <div className="bg-gradient-to-r from-blue-500 to-indigo-600 p-6 text-white">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold mb-1">Absensi Hari Ini</h2>
              <p className="text-blue-100 text-sm">
                {new Date().toLocaleDateString("id-ID", {
                  weekday: "long",
                  year: "numeric",
                  month: "long",
                  day: "numeric",
                })}
              </p>
            </div>
            <div className="w-16 h-16 bg-white/20 rounded-2xl flex items-center justify-center backdrop-blur-sm">
              <Clock className="text-white" size={32} />
            </div>
          </div>
        </div>

        <div className="p-6">
          {todayAttendance ? (
            <div className="space-y-4">
              {/* Status Card */}
              <div className="bg-gradient-to-r from-gray-50 to-blue-50/30 rounded-xl p-4 border border-gray-100">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    {getStatusIcon(todayAttendance.status)}
                    <div>
                      <p className="text-sm text-gray-600 mb-1">
                        Status Kehadiran
                      </p>
                      <span
                        className={`inline-flex items-center px-3 py-1.5 rounded-xl text-sm font-semibold border ${getStatusColor(todayAttendance.status)}`}
                      >
                        {todayAttendance.status === "present"
                          ? "Hadir"
                          : todayAttendance.status === "late"
                            ? "Terlambat"
                            : "Tidak Hadir"}
                      </span>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="text-sm text-gray-600 mb-1">Masuk</p>
                    <p className="text-2xl font-bold text-gray-900">
                      {formatTime(todayAttendance.clockIn)}
                    </p>
                  </div>
                </div>
              </div>

              {/* Clock Out Section */}
              {todayAttendance.clockOut ? (
                <div className="bg-gradient-to-r from-emerald-50 to-green-50/30 rounded-xl p-4 border border-emerald-200">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-emerald-100 rounded-xl flex items-center justify-center">
                        <CheckCircle className="text-emerald-600" size={20} />
                      </div>
                      <div>
                        <p className="text-sm text-emerald-600 font-medium mb-1">
                          Selesai
                        </p>
                        <p className="text-lg font-bold text-emerald-800">
                          {formatTime(todayAttendance.clockOut)}
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="inline-flex items-center px-3 py-1 bg-emerald-100 rounded-xl">
                        <Award className="text-emerald-600 mr-2" size={16} />
                        <span className="text-emerald-700 text-sm font-semibold">
                          Complete
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                <button
                  onClick={handleClockOut}
                  disabled={loading}
                  className="w-full bg-gradient-to-r from-red-500 to-pink-600 text-white py-4 rounded-xl hover:from-red-600 hover:to-pink-700 disabled:opacity-50 transition-all duration-200 font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
                >
                  {loading ? (
                    <div className="flex items-center justify-center">
                      <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin mr-2"></div>
                      Memproses...
                    </div>
                  ) : (
                    <div className="flex items-center justify-center">
                      <LogOut className="mr-2" size={20} />
                      Keluar Sekarang
                    </div>
                  )}
                </button>
              )}
            </div>
          ) : (
            <div className="space-y-6">
              {/* Empty State */}
              <div className="text-center py-8">
                <div className="w-24 h-24 bg-gradient-to-br from-gray-100 to-gray-200 rounded-2xl flex items-center justify-center mx-auto mb-4">
                  <AlertCircle className="text-gray-400" size={48} />
                </div>
                <h3 className="text-xl font-bold text-gray-900 mb-2">
                  Belum Absen Hari Ini
                </h3>
                <p className="text-gray-600 mb-8">
                  Mulai hari Anda dengan melakukan absensi masuk
                </p>

                {/* Action Buttons */}
                <div className="flex space-x-3 mb-6">
                  <button
                    onClick={() => setShowScanner(true)}
                    disabled={loading}
                    className="flex-1 bg-gradient-to-r from-blue-500 to-indigo-600 text-white py-4 rounded-xl hover:from-blue-600 hover:to-indigo-700 disabled:opacity-50 transition-all duration-200 font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
                  >
                    {loading ? (
                      <div className="flex items-center justify-center">
                        <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin mr-2"></div>
                        Loading...
                      </div>
                    ) : (
                      <div className="flex items-center justify-center">
                        <Camera className="mr-2" size={20} />
                        Scan QR
                      </div>
                    )}
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

// Main App Component
const App = () => {
  const [user, setUser] = useState(null);
  const [showLogin, setShowLogin] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const savedUser = localStorage.getItem("user");
    if (token && savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData.user);
  };

  const handleRegister = (userData) => {
    setUser(userData.user);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
  };

  if (user) {
    return (
      <div className="min-h-screen bg-gray-50">
        <AttendanceCard onRefresh={() => {}} />
        <div className="fixed top-4 right-4">
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition-colors"
          >
            Logout
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center">
      {showLogin ? (
        <LoginForm
          onLogin={handleLogin}
          onToggleForm={() => setShowLogin(false)}
        />
      ) : (
        <RegisterForm
          onRegister={handleRegister}
          onToggleForm={() => setShowLogin(true)}
        />
      )}
    </div>
  );
};

export default App;
