const QRScanner = ({ onScan, onClose }) => {
  const [error, setError] = useState("");

  const handleScan = (result, error) => {
    if (result) {
      onScan(result.text);
    }
    if (error) {
      setError("Error scanning QR code");
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-xl max-w-md w-full p-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-xl font-semibold">Scan QR Code</h3>
          <button
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700"
          >
            <XCircle size={24} />
          </button>
        </div>

        <div className="mb-4">
          <QrReader
            onResult={handleScan}
            constraints={{ facingMode: "environment" }}
            videoStyle={{ width: "100%" }}
          />
        </div>

        {error && (
          <div className="text-red-600 text-sm text-center">{error}</div>
        )}

        <div className="text-center text-sm text-gray-600 mt-4">
          Arahkan kamera ke QR Code untuk melakukan absensi
        </div>
      </div>
    </div>
  );
};

const AttendanceCard = ({ user, onRefresh }) => {
  const [todayAttendance, setTodayAttendance] = useState(null);
  const [qrCode, setQrCode] = useState("");
  const [showScanner, setShowScanner] = useState(false);
  const [showQR, setShowQR] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadTodayAttendance();
    loadQRCode();
  }, []);

  const loadTodayAttendance = async () => {
    try {
      const response = await attendanceService.getTodayStatus();
      setTodayAttendance(response.attendance);
    } catch (error) {
      console.error("Error loading attendance:", error);
    }
  };

  const loadQRCode = async () => {
    try {
      const response = await authService.getQRCode();
      setQrCode(response.qrCode);
    } catch (error) {
      console.error("Error loading QR code:", error);
    }
  };

  const handleScan = async (qrData) => {
    setLoading(true);
    setShowScanner(false);

    try {
      const response = await attendanceService.clockIn(qrData);
      showSuccessToast(response.msg);
      loadTodayAttendance();
      onRefresh();
    } catch (error) {
      showErrorToast(
        (error.response && error.response.data && error.response.data.msg) ||
          "Absensi gagal",
      );
    } finally {
      setLoading(false);
    }
  };

  const handleClockOut = async () => {
    setLoading(true);

    try {
      const response = await attendanceService.clockOut();
      showSuccessToast(response.msg);
      loadTodayAttendance();
      onRefresh();
    } catch (error) {
      showErrorToast(
        (error.response && error.response.data && error.response.data.msg) ||
          "Clock out gagal",
      );
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "present":
        return "text-green-600 bg-green-100";
      case "late":
        return "text-yellow-600 bg-yellow-100";
      case "absent":
        return "text-red-600 bg-red-100";
      default:
        return "text-gray-600 bg-gray-100";
    }
  };

  const formatTime = (dateString) => {
    return new Date(dateString).toLocaleTimeString("id-ID", {
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  return (
    <>
      <div className="bg-white rounded-xl shadow-lg p-6">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-2xl font-bold text-gray-900">
              Absensi Hari Ini
            </h2>
            <p className="text-gray-600">
              {new Date().toLocaleDateString("id-ID", {
                weekday: "long",
                year: "numeric",
                month: "long",
                day: "numeric",
              })}
            </p>
          </div>
          <Clock className="text-blue-600" size={32} />
        </div>

        {todayAttendance ? (
          <div className="space-y-4">
            <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
              <div>
                <p className="text-sm text-gray-600">Status</p>
                <span
                  className={`inline-block px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(todayAttendance.status)}`}
                >
                  {todayAttendance.status === "present"
                    ? "Hadir"
                    : todayAttendance.status === "late"
                      ? "Terlambat"
                      : "Tidak Hadir"}
                </span>
              </div>
              <div className="text-right">
                <p className="text-sm text-gray-600">Clock In</p>
                <p className="text-lg font-semibold">
                  {formatTime(todayAttendance.clockIn)}
                </p>
              </div>
            </div>

            {todayAttendance.clockOut ? (
              <div className="p-4 bg-green-50 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-green-600">Clock Out</p>
                    <p className="text-lg font-semibold text-green-800">
                      {formatTime(todayAttendance.clockOut)}
                    </p>
                  </div>
                  <CheckCircle className="text-green-600" size={24} />
                </div>
              </div>
            ) : (
              <button
                onClick={handleClockOut}
                disabled={loading}
                className="w-full bg-red-600 text-white py-3 rounded-lg hover:bg-red-700 disabled:opacity-50 transition-colors"
              >
                {loading ? "Processing..." : "Clock Out"}
              </button>
            )}
          </div>
        ) : (
          <div className="space-y-4">
            <div className="text-center py-8">
              <AlertCircle className="mx-auto text-gray-400 mb-4" size={48} />
              <p className="text-gray-600 mb-6">
                Belum melakukan absensi hari ini
              </p>

              <div className="flex space-x-3">
                <button
                  onClick={() => setShowScanner(true)}
                  disabled={loading}
                  className="flex-1 bg-blue-600 text-white py-3 rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-colors flex items-center justify-center"
                >
                  <Camera className="mr-2" size={20} />
                  {loading ? "Processing..." : "Scan QR Code"}
                </button>

                <button
                  onClick={() => setShowQR(!showQR)}
                  className="px-4 py-3 border border-blue-600 text-blue-600 rounded-lg hover:bg-blue-50 transition-colors"
                >
                  <Eye size={20} />
                </button>
              </div>
            </div>

            {showQR && qrCode && (
              <div className="text-center p-4 bg-gray-50 rounded-lg">
                <p className="text-sm text-gray-600 mb-4">QR Code Anda:</p>
                <div className="flex justify-center">
                  <img
                    src={qrCode}
                    alt="QR Code untuk absensi"
                    className="w-48 h-48"
                  />
                </div>
                <p className="text-xs text-gray-500 mt-2">
                  Tunjukkan QR Code ini untuk diabsen oleh admin
                </p>
              </div>
            )}
          </div>
        )}
      </div>

      {showScanner && (
        <QRScanner onScan={handleScan} onClose={() => setShowScanner(false)} />
      )}
    </>
  );
};

const PermissionForm = ({ onSuccess, onCancel, editData }) => {
  const [formData, setFormData] = useState({
    type: editData?.type || "ijin",
    startDate: editData?.startDate
      ? new Date(editData.startDate).toISOString().split("T")[0]
      : "",
    endDate: editData?.endDate
      ? new Date(editData.endDate).toISOString().split("T")[0]
      : "",
    reason: editData?.reason || "",
  });
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const formDataObj = new FormData();
      formDataObj.append("type", formData.type);
      formDataObj.append("startDate", formData.startDate);
      formDataObj.append("endDate", formData.endDate);
      formDataObj.append("reason", formData.reason);

      if (file) {
        formDataObj.append("document", file);
      }

      if (editData) {
        await permissionService.update(editData._id, formDataObj);
        showSuccessToast("Permohonan berhasil diupdate");
      } else {
        await permissionService.create(formDataObj);
        showSuccessToast("Permohonan berhasil diajukan");
      }

      onSuccess();
    } catch (error) {
      showErrorToast(
        (error.response && error.response.data && error.response.data.msg) ||
          "Terjadi kesalahan",
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-xl max-w-md w-full p-6 max-h-90vh overflow-y-auto">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-xl font-semibold">
            {editData ? "Edit" : "Ajukan"} Surat Izin/Sakit
          </h3>
          <button
            onClick={onCancel}
            className="text-gray-500 hover:text-gray-700"
          >
            <XCircle size={24} />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Jenis Permohonan
            </label>
            <select
              value={formData.type}
              onChange={(e) =>
                setFormData({ ...formData, type: e.target.value })
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            >
              <option value="ijin">Izin</option>
              <option value="sakit">Sakit</option>
              <option value="cuti">Cuti</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Tanggal Mulai
            </label>
            <input
              type="date"
              value={formData.startDate}
              onChange={(e) =>
                setFormData({ ...formData, startDate: e.target.value })
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Tanggal Selesai
            </label>
            <input
              type="date"
              value={formData.endDate}
              onChange={(e) =>
                setFormData({ ...formData, endDate: e.target.value })
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Alasan
            </label>
            <textarea
              value={formData.reason}
              onChange={(e) =>
                setFormData({ ...formData, reason: e.target.value })
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              rows={3}
              placeholder="Jelaskan alasan permohonan..."
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Dokumen Pendukung (Opsional)
            </label>
            <div className="flex items-center space-x-2">
              <input
                type="file"
                onChange={(e) => setFile(e.target.files?.[0] || null)}
                accept=".jpg,.jpeg,.png,.pdf,.doc,.docx"
                className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <Upload size={20} className="text-gray-400" />
            </div>
            <p className="text-xs text-gray-500 mt-1">
              Format: JPG, PNG, PDF, DOC, DOCX (Max 5MB)
            </p>
          </div>

          <div className="flex space-x-3 pt-4">
            <button
              type="button"
              onClick={onCancel}
              className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
            >
              Batal
            </button>
            <button
              type="submit"
              disabled={loading}
              className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-colors"
            >
              {loading ? "Menyimpan..." : editData ? "Update" : "Ajukan"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const PermissionList = () => {
  const [permissions, setPermissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editData, setEditData] = useState(null);

  useEffect(() => {
    loadPermissions();
  }, []);

  const loadPermissions = async () => {
    try {
      const response = await permissionService.getAll();
      setPermissions(response.permissions);
    } catch (error) {
      showErrorToast("Gagal memuat data permohonan");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    const result = await showConfirmDialog({
      title: "Yakin ingin menghapus permohonan ini?",
    });
    if (!result.isConfirmed) return;

    try {
      await permissionService.delete(id);
      showSuccessToast("Permohonan berhasil dihapus");
      loadPermissions();
    } catch (error) {
      showErrorToast(
        (error.response && error.response.data && error.response.data.msg) ||
          "Gagal menghapus permohonan",
      );
    }
  };

  const handleEdit = (permission) => {
    setEditData(permission);
    setShowForm(true);
  };

  const handleFormSuccess = () => {
    setShowForm(false);
    setEditData(null);
    loadPermissions();
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "approved":
        return "text-green-600 bg-green-100";
      case "rejected":
        return "text-red-600 bg-red-100";
      default:
        return "text-yellow-600 bg-yellow-100";
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case "approved":
        return "Disetujui";
      case "rejected":
        return "Ditolak";
      default:
        return "Menunggu";
    }
  };

  const getTypeText = (type) => {
    switch (type) {
      case "sakit":
        return "Sakit";
      case "cuti":
        return "Cuti";
      default:
        return "Izin";
    }
  };

  if (loading) {
    return (
      <div className="bg-white rounded-xl shadow-lg p-6">
        <div className="text-center py-8">
          <div className="animate-pulse">
            <div className="h-4 bg-gray-200 rounded w-1/4 mx-auto mb-4"></div>
            <div className="h-4 bg-gray-200 rounded w-1/2 mx-auto"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="bg-white rounded-xl shadow-lg p-6">
        <div className="flex justify-between items-center mb-6">
          <div>
            <h2 className="text-2xl font-bold text-gray-900">
              Surat Izin/Sakit
            </h2>
            <p className="text-gray-600">
              Kelola permohonan izin dan sakit Anda
            </p>
          </div>
          <button
            onClick={() => setShowForm(true)}
            className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors flex items-center"
          >
            <FileText className="mr-2" size={20} />
            Ajukan Baru
          </button>
        </div>

        {permissions.length === 0 ? (
          <div className="text-center py-8">
            <FileText className="mx-auto text-gray-400 mb-4" size={48} />
            <p className="text-gray-600">Belum ada permohonan izin/sakit</p>
          </div>
        ) : (
          <div className="space-y-4">
            {permissions.map((permission) => (
              <div
                key={permission._id}
                className="border border-gray-200 rounded-lg p-4"
              >
                <div className="flex justify-between items-start mb-3">
                  <div>
                    <div className="flex items-center space-x-2 mb-2">
                      <span className="font-medium text-gray-900">
                        {getTypeText(permission.type)}
                      </span>
                      <span
                        className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(permission.status)}`}
                      >
                        {getStatusText(permission.status)}
                      </span>
                    </div>
                    <p className="text-sm text-gray-600">
                      {new Date(permission.startDate).toLocaleDateString()} -{" "}
                      {new Date(permission.endDate).toLocaleDateString()}
                    </p>
                    <p className="text-sm text-gray-800 mt-1">
                      {permission.reason}
                    </p>
                    {permission.document && (
                      <a
                        href={`http://localhost:5000/${permission.document.path}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-500 hover:underline text-sm flex items-center mt-2"
                      >
                        <Download size={16} className="mr-1" />
                        Lihat Dokumen
                      </a>
                    )}
                  </div>
                  {permission.status === "pending" && (
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleEdit(permission)}
                        className="text-blue-600 hover:text-blue-800"
                      >
                        <Edit size={16} />
                      </button>
                      <button
                        onClick={() => handleDelete(permission._id)}
                        className="text-red-600 hover:text-red-800"
                      >
                        <Trash2 size={16} />
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {showForm && (
        <PermissionForm
          onSuccess={handleFormSuccess}
          onCancel={() => {
            setShowForm(false);
            setEditData(null);
          }}
          editData={editData}
        />
      )}
    </>
  );
};

