              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {attendance.map((att) => (
                <tr key={att._id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    {att.user?.name} ({att.user?.employeeId})
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(att.date).toLocaleDateString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(att.clockIn).toLocaleTimeString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {att.clockOut
                      ? new Date(att.clockOut).toLocaleTimeString()
                      : "-"}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span
                      className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(att.status)}`}
                    >
                      {att.status === "present"
                        ? "Hadir"
                        : att.status === "late"
                          ? "Terlambat"
                          : "Tidak Hadir"}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

const UserDashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState("attendance");
  const [refreshKey, setRefreshKey] = useState(0);

  const handleRefresh = () => {
    setRefreshKey((prev) => prev + 1);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <nav className="bg-white shadow-sm py-4 px-6 flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <h1 className="text-2xl font-bold text-gray-900">Absensi QR</h1>
          <span className="text-gray-600">
            Halo, {user.name} ({user.role})
          </span>
        </div>
        <button
          onClick={onLogout}
          className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition-colors flex items-center"
        >
          <LogOut size={20} className="mr-2" />
          Logout
        </button>
      </nav>

      <div className="container mx-auto p-6">
        <div className="bg-white rounded-xl shadow-lg p-4 mb-6 flex space-x-4">
          <button
            onClick={() => setActiveTab("attendance")}
            className={`flex-1 py-2 px-4 rounded-lg text-center font-medium ${activeTab === "attendance" ? "bg-blue-600 text-white" : "text-gray-700 hover:bg-gray-100"}`}
          >
            <Clock className="inline-block mr-2" size={20} /> Absensi
          </button>
          <button
            onClick={() => setActiveTab("permission")}
            className={`flex-1 py-2 px-4 rounded-lg text-center font-medium ${activeTab === "permission" ? "bg-blue-600 text-white" : "text-gray-700 hover:bg-gray-100"}`}
          >
            <FileText className="inline-block mr-2" size={20} /> Izin/Sakit
          </button>
          {user.role === "admin" && (
            <button
              onClick={() => setActiveTab("admin")}
              className={`flex-1 py-2 px-4 rounded-lg text-center font-medium ${activeTab === "admin" ? "bg-blue-600 text-white" : "text-gray-700 hover:bg-gray-100"}`}
            >
              <Settings className="inline-block mr-2" size={20} /> Admin
            </button>
          )}
        </div>

        <div>
          {activeTab === "attendance" && (
            <AttendanceCard
              user={user}
              onRefresh={handleRefresh}
              key={`attendance-${refreshKey}`}
            />
          )}
          {activeTab === "permission" && (
            <PermissionList key={`permission-${refreshKey}`} />
          )}
          {activeTab === "admin" && user.role === "admin" && (
            <AdminDashboard key={`admin-${refreshKey}`} />
          )}
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const [user, setUser] = useState(authService.getCurrentUser());
  const [showRegister, setShowRegister] = useState(false);

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    if (currentUser) {
      setUser(currentUser);
    }
  }, []);

  const handleLogin = (loggedInUser) => {
    setUser(loggedInUser);
    setShowRegister(false);
  };

  const handleRegister = (registeredUser) => {
    setUser(registeredUser);
    setShowRegister(false);
  };

  const handleLogout = () => {
    authService.logout();
    setUser(null);
    setShowRegister(false);
  };

  if (user) {
    return <UserDashboard user={user} onLogout={handleLogout} />;
  }

  return showRegister ? (
    <RegisterForm
      onRegister={handleRegister}
      onToggleForm={() => setShowRegister(false)}
    />
  ) : (
    <LoginForm
      onLogin={handleLogin}
      onToggleForm={() => setShowRegister(true)}
    />
  );
};

export default App;
