};

// Modern Login Form Component
const ModernLoginForm = ({ onLogin, onToggleForm }) => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.email) {
      newErrors.email = "Email wajib diisi";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "Format email tidak valid";
    }
    
    if (!formData.password) {
      newErrors.password = "Password wajib diisi";
    } else if (formData.password.length < 6) {
      newErrors.password = "Password minimal 6 karakter";
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setLoading(true);
    setErrors({});

    try {
      const response = await authService.login(
        formData.email,
        formData.password,
      );
      showSuccessToast("Login berhasil!");
      onLogin(response.user);
    } catch (error) {
      const errorMessage = (error.response && error.response.data && error.response.data.msg) || "Login gagal";
      showErrorToast(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4 relative overflow-hidden">
      {/* Animated Background */}
      <div className="absolute inset-0">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-blue-500 rounded-full opacity-10 animate-pulse"></div>
        <div className="absolute bottom-1/4 right-1/4 w-80 h-80 bg-purple-500 rounded-full opacity-10 animate-pulse" style={{ animationDelay: '1s' }}></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-64 bg-indigo-500 rounded-full opacity-5 animate-pulse" style={{ animationDelay: '2s' }}></div>
      </div>

      {/* Main Login Card */}
      <div className="relative z-10 w-full max-w-md">
        <div className="modern-card animate-slideUp">
          
          {/* Header */}
          <div className="modern-card-header text-center">
            <div className="w-20 h-20 mx-auto mb-6 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-2xl flex items-center justify-center shadow-lg">
              <QrCode className="w-10 h-10 text-white" />
            </div>
            <h1 className="text-3xl font-bold text-gradient mb-2">Absensi QR</h1>
            <p className="text-gray-600">Sistem Kehadiran Digital Modern</p>
          </div>

          {/* Login Form */}
          <div className="modern-card-body">
            <form onSubmit={handleSubmit} className="space-y-6">
              
              {/* Email Input */}
              <div className="form-group">
                <label className="form-label">Email Address</label>
                <div className="input-group">
                  <input
                    type="email"
                    required
                    className={`form-input ${errors.email ? 'border-red-500' : ''}`}
                    value={formData.email}
                    onChange={(e) => {
                      setFormData({ ...formData, email: e.target.value });
                      if (errors.email) setErrors({ ...errors, email: null });
                    }}
                    placeholder="Enter your email"
                  />
                  <div className="input-icon">
                    <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 12a4 4 0 10-8 0 4 4 0 008 0zm0 0v1.5a2.5 2.5 0 005 0V12a9 9 0 10-9 9m4.5-1.206a8.959 8.959 0 01-4.5 1.207" />
                    </svg>
                  </div>
                </div>
                {errors.email && (
                  <p className="mt-2 text-sm text-red-600 flex items-center">
                    <AlertCircle className="w-4 h-4 mr-1" />
                    {errors.email}
                  </p>
                )}
              </div>

              {/* Password Input */}
              <div className="form-group">
                <label className="form-label">Password</label>
                <div className="input-group">
                  <input
                    type={showPassword ? "text" : "password"}
                    required
                    className={`form-input ${errors.password ? 'border-red-500' : ''}`}
                    value={formData.password}
                    onChange={(e) => {
                      setFormData({ ...formData, password: e.target.value });
                      if (errors.password) setErrors({ ...errors, password: null });
                    }}
                    placeholder="Enter your password"
                  />
                  <div className="input-icon">
                    <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
                    </svg>
                  </div>
                  <button
                    type="button"
                    className="absolute inset-y-0 right-0 pr-4 flex items-center"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? (
                      <svg className="h-5 w-5 text-gray-400 hover:text-gray-600 transition-colors" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.878 9.878L8.464 8.464m1.414 1.414L8.464 8.464m5.656 5.656l1.414 1.414m-1.414-1.414l1.414 1.414M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                      </svg>
                    ) : (
                      <svg className="h-5 w-5 text-gray-400 hover:text-gray-600 transition-colors" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                      </svg>
                    )}
                  </button>
                </div>
                {errors.password && (
                  <p className="mt-2 text-sm text-red-600 flex items-center">
                    <AlertCircle className="w-4 h-4 mr-1" />
                    {errors.password}
                  </p>
                )}
              </div>

              {/* Login Button */}
              <button
                type="submit"
                disabled={loading}
                className="btn btn-primary btn-lg w-full"
              >
                {loading ? (
                  <>
                    <div className="loading-spinner"></div>
                    Processing...
                  </>
                ) : (
                  <>
                    <LogOut className="w-5 h-5" />
                    Sign In
                  </>
                )}
              </button>

              {/* Register Link */}
              <div className="text-center">
                <p className="text-gray-600 text-sm">
                  Belum punya akun?{" "}
                  <button
                    type="button"
                    onClick={onToggleForm}
                    className="text-blue-600 hover:text-blue-700 font-semibold transition-colors hover:underline"
                  >
                    Daftar sekarang
                  </button>
                </p>
              </div>
            </form>
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-6 animate-fadeIn" style={{ animationDelay: "0.5s" }}>
          <p className="text-white/70 text-sm">
             2024 Absensi QR. Sistem absensi modern & terpercaya.
          </p>
        </div>
      </div>
    </div>
  );
};

// Modern Register Form Component
const ModernRegisterForm = ({ onRegister, onToggleForm }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    employeeId: "",
    department: "",
  });
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await authService.register(formData);
      showSuccessToast("Registrasi berhasil!");
      onRegister(response.user);
    } catch (error) {
      showErrorToast(
        (error.response && error.response.data && error.response.data.msg) ||
          "Registrasi gagal",
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4 relative overflow-hidden">
      {/* Animated Background */}
      <div className="absolute inset-0">
        <div className="absolute top-1/4 right-1/4 w-96 h-96 bg-emerald-500 rounded-full opacity-10 animate-pulse"></div>
        <div className="absolute bottom-1/4 left-1/4 w-80 h-80 bg-green-500 rounded-full opacity-10 animate-pulse" style={{ animationDelay: '1s' }}></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-64 bg-teal-500 rounded-full opacity-5 animate-pulse" style={{ animationDelay: '2s' }}></div>
      </div>

      {/* Main Register Card */}
      <div className="relative z-10 w-full max-w-md">
        <div className="modern-card animate-slideUp">
          
          {/* Header */}
          <div className="modern-card-header text-center">
            <div className="w-20 h-20 mx-auto mb-6 bg-gradient-to-r from-emerald-600 to-green-600 rounded-2xl flex items-center justify-center shadow-lg">
              <Users className="w-10 h-10 text-white" />
            </div>
            <h1 className="text-3xl font-bold text-gradient mb-2">Daftar Akun</h1>
            <p className="text-gray-600">Bergabung dengan sistem absensi modern</p>
          </div>

          {/* Register Form */}
          <div className="modern-card-body">
            <form onSubmit={handleSubmit} className="space-y-5">
              
              {/* Name Field */}
              <div className="form-group">
                <label className="form-label">Nama Lengkap</label>
                <div className="input-group">
                  <input
                    type="text"
                    required
                    className="form-input"
                    value={formData.name}
                    onChange={(e) =>
                      setFormData({ ...formData, name: e.target.value })
                    }
                    placeholder="John Doe"
                  />
                  <div className="input-icon">
                    <Users className="h-5 w-5" />
                  </div>
                </div>
              </div>

              {/* Email Field */}
              <div className="form-group">
                <label className="form-label">Email Address</label>
                <div className="input-group">
                  <input
                    type="email"
                    required
                    className="form-input"
                    value={formData.email}
                    onChange={(e) =>
                      setFormData({ ...formData, email: e.target.value })
                    }
                    placeholder="john@email.com"
                  />
                  <div className="input-icon">
                    <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 12a4 4 0 10-8 0 4 4 0 008 0zm0 0v1.5a2.5 2.5 0 005 0V12a9 9 0 10-9 9m4.5-1.206a8.959 8.959 0 01-4.5 1.207" />
                    </svg>
                  </div>
                </div>
              </div>

              {/* Password Field */}
              <div className="form-group">
                <label className="form-label">Password</label>
                <div className="input-group">
                  <input
                    type={showPassword ? "text" : "password"}
                    required
                    minLength={6}
                    className="form-input"
                    value={formData.password}
                    onChange={(e) =>
                      setFormData({ ...formData, password: e.target.value })
                    }
                    placeholder="Minimal 6 karakter"
                  />
                  <div className="input-icon">
                    <Shield className="h-5 w-5" />
                  </div>
                  <button
                    type="button"
                    className="absolute inset-y-0 right-0 pr-4 flex items-center"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? <Eye className="h-5 w-5 text-gray-400" /> : <Eye className="h-5 w-5 text-gray-400" />}
                  </button>
                </div>
              </div>

              {/* Employee ID Field */}
              <div className="form-group">
                <label className="form-label">ID Karyawan</label>
                <div className="input-group">
                  <input
                    type="text"
                    required
                    className="form-input"
                    value={formData.employeeId}
                    onChange={(e) =>
                      setFormData({
                        ...formData,
                        employeeId: e.target.value.toUpperCase(),
                      })
                    }
                    placeholder="EMP001"
                  />
                  <div className="input-icon">
                    <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H5a2 2 0 00-2 2v9a2 2 0 002 2h14a2 2 0 002-2V8a2 2 0 00-2-2h-5m-4 0V5a2 2 0 114 0v1m-4 0a2 2 0 104 0m-5 8a2 2 0 100-4 2 2 0 000 4zm0 0c1.306 0 2.417.835 2.83 2M9 14a3.001 3.001 0 00-2.83 2M15 11h3m-3 4h2" />
                    </svg>
                  </div>
                </div>
              </div>

              {/* Department Field */}
              <div className="form-group">
                <label className="form-label">Departemen</label>
                <div className="input-group">
                  <input
                    type="text"
                    className="form-input"
                    value={formData.department}
                    onChange={(e) =>
                      setFormData({ ...formData, department: e.target.value })
                    }
                    placeholder="IT / HR / Finance (opsional)"
                  />
                  <div className="input-icon">
                    <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                    </svg>
                  </div>
                </div>
              </div>

              {/* Submit Button */}
              <button
                type="submit"
                disabled={loading}
                className="btn btn-success btn-lg w-full"
              >
                {loading ? (
                  <>
                    <div className="loading-spinner"></div>
                    Memproses...
                  </>
                ) : (
                  <>
                    <Users className="w-5 h-5" />
                    Buat Akun Baru
                  </>
                )}
              </button>

              {/* Login Link */}
              <div className="text-center">
                <p className="text-gray-600 text-sm">
                  Sudah punya akun?{" "}
                  <button
                    type="button"
                    onClick={onToggleForm}
                    className="text-emerald-600 hover:text-emerald-700 font-semibold transition-colors hover:underline"
                  >
                    Login sekarang
                  </button>
                </p>
              </div>
            </form>
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-6 animate-fadeIn" style={{ animationDelay: "0.5s" }}>
          <p className="text-white/70 text-sm">
             2024 Absensi QR. Sistem absensi modern & terpercaya.
          </p>
        </div>
      </div>
    </div>
  );
};

// Main Modern App Component
const ModernApp = () => {
  const [user, setUser] = useState(authService.getCurrentUser());
  const [showRegister, setShowRegister] = useState(false);

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    if (currentUser) {
      setUser(currentUser);
    }
  }, []);

  const handleLogin = (loggedInUser) => {
    setUser(loggedInUser);
    setShowRegister(false);
  };

  const handleRegister = (registeredUser) => {
    setUser(registeredUser);
    setShowRegister(false);
  };

  const handleLogout = () => {
    authService.logout();
    setUser(null);
    setShowRegister(false);
  };

  if (user) {
    // For now, just show a simple dashboard placeholder
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
        <nav className="modern-nav">
          <div className="modern-nav-content">
            <div className="modern-nav-brand">
              <div className="modern-nav-brand-icon">
                <QrCode size={20} />
              </div>
              <span>Absensi QR</span>
            </div>
            <div className="modern-nav-user">
              <div className="text-right">
                <p className="text-sm font-medium text-gray-900">{user.name}</p>
                <p className="text-xs text-gray-500">{user.role}  {user.employeeId}</p>
              </div>
              <div className="modern-nav-avatar">
                {user.name.charAt(0).toUpperCase()}
              </div>
              <button
                onClick={handleLogout}
                className="btn btn-error btn-sm"
              >
                <LogOut size={16} />
                Logout
              </button>
            </div>
          </div>
        </nav>

        <div className="container mx-auto p-6 max-w-7xl">
          <div className="modern-card animate-slideUp">
            <div className="modern-card-header">
              <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
              <p className="text-gray-600">Selamat datang di sistem absensi modern</p>
            </div>
            <div className="modern-card-body">
              <div className="text-center py-12">
                <QrCode className="mx-auto text-blue-500 mb-4" size={64} />
                <h2 className="text-2xl font-bold text-gray-900 mb-2">
                  Sistem Absensi QR Modern
                </h2>
                <p className="text-gray-600 mb-6">
                  Dashboard lengkap sedang dalam pengembangan. Fitur login dan registrasi telah berhasil dimodernisasi!
                </p>
                <div className="flex justify-center space-x-4">
                  <div className="stats-card">
                    <div className="stats-card-header">
                      <div className="stats-card-title">Status</div>
                      <div className="stats-card-icon">
                        <CheckCircle size={24} />
                      </div>
                    </div>
                    <div className="stats-card-value">Online</div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return showRegister ? (
    <ModernRegisterForm
      onRegister={handleRegister}
      onToggleForm={() => setShowRegister(false)}
    />
  ) : (
    <ModernLoginForm
      onLogin={handleLogin}
      onToggleForm={() => setShowRegister(true)}
    />
  );
};

export default ModernApp;
