import React, { useState, useEffect } from 'react';
import axios from 'axios';

import { QrReader } from 'react-qr-reader';
import { showSuccessToast, showErrorToast, showConfirmDialog } from './utils/swal.js';
import {
  Clock,
  FileText,
  Settings,
  LogOut,
  Users,
  CheckCircle,
  XCircle,
  AlertCircle,
  Camera,
  Upload,
  Download,
  Eye,
  Edit,
  Trash2
} from 'lucide-react';

// API Service
const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
});

// Add token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Auth Service
const authService = {
  login: async (email, password) => {
    const response = await api.post('/auth/login', { email, password });
    if (response.data.token) {
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));
    }
    return response.data;
  },

  register: async (userData) => {
    const response = await api.post('/auth/register', userData);
    if (response.data.token) {
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));
    }
    return response.data;
  },

  logout: () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  },

  getCurrentUser: () => {
    const user = localStorage.getItem('user');
    return user ? JSON.parse(user) : null;
  },

  getQRCode: async () => {
    const response = await api.get('/auth/qr-code');
    return response.data;
  }
};

// Attendance Service
const attendanceService = {
  clockIn: async (qrCodeData, location) => {
    const response = await api.post("/attendance/clock-in", { qrCodeData, location });
    return response.data;
  },

  clockOut: async () => {
    const response = await api.post("/attendance/clock-out");
    return response.data;
  },

  getHistory: async (params) => {
    const response = await api.get("/attendance/history", { params });
    return response.data;
  },

  getTodayStatus: async () => {
    const response = await api.get('/attendance/today');
    return response.data;
  }
};

// Permission Service
const permissionService = {
  create: async (formData) => {
    const response = await api.post('/permission', formData, {
      headers: { 'Content-Type': 'multipart/form-data' }
    });
    return response.data;
  },

  getAll: async (params) => {
    const response = await api.get('/permission', { params });
    return response.data;
  },

  getById: async (id) => {
    const response = await api.get(`/permission/${id}`);
    return response.data;
  },

  update: async (id, formData) => {
    const response = await api.put(`/permission/${id}`, formData, {
      headers: { 'Content-Type': 'multipart/form-data' }
    });
    return response.data;
  },

  delete: async (id) => {
    const response = await api.delete(`/permission/${id}`);
    return response.data;
  }
};

// Admin Service
const adminService = {
  getStats: async () => {
    const response = await api.get('/admin/stats');
    return response.data;
  },

  getAllUsers: async (params) => {
    const response = await api.get('/admin/users', { params });
    return response.data;
  },

  getAllAttendance: async (params) => {
    const response = await api.get('/admin/attendance', { params });
    return response.data;
  },

  getAllPermissions: async (params) => {
    const response = await api.get('/admin/permissions', { params });
    return response.data;
  },

  approvePermission: async (id, status, notes) => {
    const response = await api.put(`/admin/permissions/${id}`, { status, approvalNotes: notes });
    return response.data;
  },

  toggleUser: async (id) => {
    const response = await api.put(`/admin/users/${id}/toggle`);
    return response.data;
  }
};

// Components
const LoginForm = ({ onLogin, onToggleForm }) => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await authService.login(formData.email, formData.password);
      showSuccessToast('Login berhasil!');
      onLogin(response.user);
    } catch (error) {
      showErrorToast((error.response && error.response.data && error.response.data.msg) || 'Login gagal');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="max-w-md w-full bg-white rounded-xl shadow-lg p-8">
        <div className="text-center mb-8">
