const PermissionList = () => {
  const [permissions, setPermissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editData, setEditData] = useState(null);

  useEffect(() => {
    loadPermissions();
  }, []);

  const loadPermissions = async () => {
    try {
      const response = await permissionService.getAll();
      setPermissions(response.permissions);
    } catch (error) {
      showErrorToast('Gagal memuat data permohonan');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    const result = await showConfirmDialog({ title: 'Yakin ingin menghapus permohonan ini?' });
    if (!result.isConfirmed) return;

    try {
      await permissionService.delete(id);
      showSuccessToast('Permohonan berhasil dihapus');
      loadPermissions();
    } catch (error) {
      showErrorToast((error.response && error.response.data && error.response.data.msg) || 'Gagal menghapus permohonan');
    }
  };

  const handleEdit = (permission) => {
    setEditData(permission);
    setShowForm(true);
  };

  const handleFormSuccess = () => {
    setShowForm(false);
    setEditData(null);
    loadPermissions();
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved': return 'text-green-600 bg-green-100';
      case 'rejected': return 'text-red-600 bg-red-100';
      default: return 'text-yellow-600 bg-yellow-100';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'approved': return 'Disetujui';
      case 'rejected': return 'Ditolak';
      default: return 'Menunggu';
    }
  };

  const getTypeText = (type: string) => {
    switch (type) {
      case 'sakit': return 'Sakit';
      case 'cuti': return 'Cuti';
      default: return 'Izin';
    }
  };

  if (loading) {
    return (
      <div className="bg-white rounded-xl shadow-lg p-6">
        <div className="text-center py-8">
          <div className="animate-pulse">
            <div className="h-4 bg-gray-200 rounded w-1/4 mx-auto mb-4"></div>
            <div className="h-4 bg-gray-200 rounded w-1/2 mx-auto"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="bg-white rounded-xl shadow-lg p-6">
        <div className="flex justify-between items-center mb-6">
          <div>
            <h2 className="text-2xl font-bold text-gray-900">Surat Izin/Sakit</h2>
            <p className="text-gray-600">Kelola permohonan izin dan sakit Anda</p>
          </div>
          <button
            onClick={() => setShowForm(true)}
            className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors flex items-center"
          >
            <FileText className="mr-2" size={20} />
            Ajukan Baru
          </button>
        </div>

        {permissions.length === 0 ? (
          <div className="text-center py-8">
            <FileText className="mx-auto text-gray-400 mb-4" size={48} />
            <p className="text-gray-600">Belum ada permohonan izin/sakit</p>
          </div>
        ) : (
          <div className="space-y-4">
            {permissions.map((permission) => (
              <div key={permission._id} className="border border-gray-200 rounded-lg p-4">
                <div className="flex justify-between items-start mb-3">
                  <div>
                    <div className="flex items-center space-x-2 mb-2">
                      <span className="font-medium text-gray-900">
                        {getTypeText(permission.type)}
                      </span>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(permission.status)}`}>
                        {getStatusText(permission.status)}
                      </span>
                    </div>
                    <p className="text-sm text-gray-600">
                      {new Date(permission.startDate).toLocaleDateString()} - {new Date(permission.endDate).toLocaleDateString()}
                    </p>
                    <p className="text-sm text-gray-800 mt-1">{permission.reason}</p>
                    {permission.document && (
                      <a
                        href={`http://localhost:5000/${permission.document.path}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-500 hover:underline text-sm flex items-center mt-2"
                      >
                        <Download size={16} className="mr-1" />
                        Lihat Dokumen
                      </a>
                    )}
                  </div>
                  {permission.status === 'pending' && (
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleEdit(permission)}
                        className="text-blue-600 hover:text-blue-800"
                      >
                        <Edit size={16} />
                      </button>
                      <button
                        onClick={() => handleDelete(permission._id)}
                        className="text-red-600 hover:text-red-800"
                      >
                        <Trash2 size={16} />
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {showForm && (
        <PermissionForm
          onSuccess={handleFormSuccess}
          onCancel={() => {
            setShowForm(false);
            setEditData(null);
          }}
          editData={editData}
        />
      )}
    </>
  );
};

const AdminDashboard = () => {
  const [stats, setStats] = useState(null);
  const [users, setUsers] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [permissions, setPermissions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadAdminData();
  }, []);

  const loadAdminData = async () => {
    try {
      const [statsRes, usersRes, attendanceRes, permissionsRes] = await Promise.all([
        adminService.getStats(),
        adminService.getAllUsers(),
        adminService.getAllAttendance(),
        adminService.getAllPermissions()
      ]);
      setStats(statsRes);
      setUsers(usersRes.users);
      setAttendance(attendanceRes.attendances);
      setPermissions(permissionsRes.permissions);
    } catch (error) {
      showErrorToast('Gagal memuat data admin');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveReject = async (id: string, status: string) => {
    const result = await showConfirmDialog({ title: `Yakin ingin ${status === 'approved' ? 'menyetujui' : 'menolak'} permohonan ini?` });
    if (!result.isConfirmed) return;
    try {
      await adminService.approvePermission(id, status);
      showSuccessToast(`Permohonan berhasil di${status === 'approved' ? 'setujui' : 'tolak'}`);
      loadAdminData();
    } catch (error) {
      showErrorToast((error.response && error.response.data && error.response.data.msg) || 'Gagal memproses permohonan');
    }
  };

  const handleToggleUser = async (id: string) => {
    const result = await showConfirmDialog({ title: 'Yakin ingin mengubah status user ini?' });
    if (!result.isConfirmed) return;
    try {
      await adminService.toggleUser(id);
      showSuccessToast('Status user berhasil diubah');
      loadAdminData();
    } catch (error) {
      showErrorToast((error.response && error.response.data && error.response.data.msg) || 'Gagal mengubah status user');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved': return 'text-green-600 bg-green-100';
      case 'rejected': return 'text-red-600 bg-red-100';
      default: return 'text-yellow-600 bg-yellow-100';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'approved': return 'Disetujui';
      case 'rejected': return 'Ditolak';
      default: return 'Menunggu';
    }
  };

  const getTypeText = (type: string) => {
    switch (type) {
      case 'sakit': return 'Sakit';
      case 'cuti': return 'Cuti';
      default: return 'Izin';
    }
  };

  if (loading) {
    return (
      <div className="p-6 text-center">
        <p>Loading Admin Dashboard...</p>
      </div>
    );
  }

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-gray-900 mb-6">Admin Dashboard</h1>

      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-xl shadow-lg p-6 flex items-center justify-between">
            <div>
              <p className="text-gray-600">Total Pengguna</p>
              <p className="text-3xl font-bold text-gray-900">{stats.totalUsers}</p>
            </div>
            <Users size={48} className="text-blue-500" />
          </div>
          <div className="bg-white rounded-xl shadow-lg p-6 flex items-center justify-between">
            <div>
              <p className="text-gray-600">Absensi Hari Ini</p>
              <p className="text-3xl font-bold text-gray-900">{stats.todayAttendance}</p>
            </div>
            <CheckCircle size={48} className="text-green-500" />
          </div>
          <div className="bg-white rounded-xl shadow-lg p-6 flex items-center justify-between">
            <div>
              <p className="text-gray-600">Izin Menunggu</p>
              <p className="text-3xl font-bold text-gray-900">{stats.pendingPermissions}</p>
            </div>
            <AlertCircle size={48} className="text-yellow-500" />
          </div>
          <div className="bg-white rounded-xl shadow-lg p-6 flex items-center justify-between">
            <div>
              <p className="text-gray-600">Total Izin</p>
              <p className="text-3xl font-bold text-gray-900">{stats.totalPermissions}</p>
            </div>
            <FileText size={48} className="text-indigo-500" />
          </div>
        </div>
      )}

      <div className="bg-white rounded-xl shadow-lg p-6 mb-8">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Manajemen Pengguna</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Nama</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID Karyawan</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Role</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Aksi</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {users.map((user: any) => (
                <tr key={user._id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{user.name}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{user.email}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{user.employeeId}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{user.role}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${user.isActive ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                      {user.isActive ? 'Aktif' : 'Nonaktif'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    {user.role !== 'admin' && (
                      <button
                        onClick={() => handleToggleUser(user._id)}
                        className={`${user.isActive ? 'text-red-600 hover:text-red-900' : 'text-green-600 hover:text-green-900'} mr-3`}
                      >
                        {user.isActive ? 'Nonaktifkan' : 'Aktifkan'}
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div className="bg-white rounded-xl shadow-lg p-6 mb-8">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Permohonan Izin/Sakit</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Karyawan</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Tipe</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Tanggal</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Alasan</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Dokumen</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Aksi</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {permissions.map((permission: any) => (
                <tr key={permission._id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{permission.user?.name} ({permission.user?.employeeId})</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{getTypeText(permission.type)}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{new Date(permission.startDate).toLocaleDateString()} - {new Date(permission.endDate).toLocaleDateString()}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{permission.reason}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {permission.document && (
                      <a
                        href={`http://localhost:5000/${permission.document.path}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-500 hover:underline flex items-center"
                      >
                        <Eye size={16} className="mr-1" /> Lihat
                      </a>
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(permission.status)}`}>
                      {getStatusText(permission.status)}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    {permission.status === 'pending' && (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleApproveReject(permission._id, 'approved')}
                          className="text-green-600 hover:text-green-900"
                        >
                          Setujui
                        </button>
                        <button
                          onClick={() => handleApproveReject(permission._id, 'rejected')}
                          className="text-red-600 hover:text-red-900"
                        >
                          Tolak
                        </button>
                      </div>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div className="bg-white rounded-xl shadow-lg p-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Riwayat Absensi</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Karyawan</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Tanggal</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Clock In</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Clock Out</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {attendance.map((att: any) => (
                <tr key={att._id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{att.user?.name} ({att.user?.employeeId})</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{new Date(att.date).toLocaleDateString()}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{new Date(att.clockIn).toLocaleTimeString()}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{att.clockOut ? new Date(att.clockOut).toLocaleTimeString() : '-'}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(att.status)}`}>
                      {att.status === 'present' ? 'Hadir' : att.status === 'late' ? 'Terlambat' : 'Tidak Hadir'}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

const UserDashboard: React.FC<{ user: any; onLogout: () => void }> = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('attendance');
  const [refreshKey, setRefreshKey] = useState(0);

  const handleRefresh = () => {
    setRefreshKey(prev => prev + 1);
  };

  return (
    <div className="min-h-screen bg-gray-100">

      <nav className="bg-white shadow-sm py-4 px-6 flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <h1 className="text-2xl font-bold text-gray-900">Absensi QR</h1>
          <span className="text-gray-600">Halo, {user.name} ({user.role})</span>
        </div>
        <button
          onClick={onLogout}
          className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition-colors flex items-center"
        >
          <LogOut size={20} className="mr-2" />
          Logout
        </button>
      </nav>

      <div className="container mx-auto p-6">
        <div className="bg-white rounded-xl shadow-lg p-4 mb-6 flex space-x-4">
          <button
            onClick={() => setActiveTab('attendance')}
            className={`flex-1 py-2 px-4 rounded-lg text-center font-medium ${activeTab === 'attendance' ? 'bg-blue-600 text-white' : 'text-gray-700 hover:bg-gray-100'}`}
          >
            <Clock className="inline-block mr-2" size={20} /> Absensi
          </button>
          <button
            onClick={() => setActiveTab('permission')}
            className={`flex-1 py-2 px-4 rounded-lg text-center font-medium ${activeTab === 'permission' ? 'bg-blue-600 text-white' : 'text-gray-700 hover:bg-gray-100'}`}
          >
            <FileText className="inline-block mr-2" size={20} /> Izin/Sakit
          </button>
          {user.role === 'admin' && (
            <button
              onClick={() => setActiveTab('admin')}
              className={`flex-1 py-2 px-4 rounded-lg text-center font-medium ${activeTab === 'admin' ? 'bg-blue-600 text-white' : 'text-gray-700 hover:bg-gray-100'}`}
            >
              <Settings className="inline-block mr-2" size={20} /> Admin
            </button>
          )}
        </div>

        <div>
          {activeTab === 'attendance' && <AttendanceCard user={user} onRefresh={handleRefresh} key={`attendance-${refreshKey}`} />}
          {activeTab === 'permission' && <PermissionList key={`permission-${refreshKey}`} />}
          {activeTab === 'admin' && user.role === 'admin' && <AdminDashboard key={`admin-${refreshKey}`} />}
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const [user, setUser] = useState(authService.getCurrentUser());
  const [showRegister, setShowRegister] = useState(false);

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    if (currentUser) {
      setUser(currentUser);
    }
  }, []);

  const handleLogin = (loggedInUser) => {
    setUser(loggedInUser);
    setShowRegister(false);
  };

  const handleRegister = (registeredUser) => {
    setUser(registeredUser);
    setShowRegister(false);
  };

  const handleLogout = () => {
    authService.logout();
    setUser(null);
    setShowRegister(false);
  };

  if (user) {
    return <UserDashboard user={user} onLogout={handleLogout} />;
  }

  return showRegister ? (
    <RegisterForm onRegister={handleRegister} onToggleForm={() => setShowRegister(false)} />
  ) : (
    <LoginForm onLogin={handleLogin} onToggleForm={() => setShowRegister(true)} />
  );
};

export default App;
