// Simple alert utilities without SweetAlert2

export const showSuccess = (title, text = "") => {
  alert(`‚úÖ ${title}${text ? "\n" + text : ""}`);
  return Promise.resolve({ isConfirmed: true });
};

export const showError = (title, text = "") => {
  alert(`‚ùå ${title}${text ? "\n" + text : ""}`);
  return Promise.resolve({ isConfirmed: true });
};

export const showWarning = (title, text = "") => {
  alert(`‚ö†Ô∏è ${title}${text ? "\n" + text : ""}`);
  return Promise.resolve({ isConfirmed: true });
};

export const showInfo = (title, text = "") => {
  alert(`‚ÑπÔ∏è ${title}${text ? "\n" + text : ""}`);
  return Promise.resolve({ isConfirmed: true });
};

export const showConfirm = (
  title,
  text = "",
  confirmText = "OK",
  cancelText = "Cancel",
) => {
  const result = confirm(`${title}${text ? "\n" + text : ""}`);
  return Promise.resolve({ isConfirmed: result });
};

export const showLoading = (title = "Loading...") => {
  console.log(`üîÑ ${title}`);
  return Promise.resolve();
};

export const closeLoading = () => {
  console.log("Loading closed");
};

export const showToast = (title, icon = "success") => {
  const emoji = icon === "success" ? "‚úÖ" : icon === "error" ? "‚ùå" : "‚ÑπÔ∏è";
  console.log(`${emoji} ${title}`);
  return Promise.resolve();
};

export default {
  showSuccess,
  showError,
  showWarning,
  showInfo,
  showConfirm,
  showLoading,
  closeLoading,
  showToast,
};
